'function f_render (',
'    p_region              in apex_plugin.t_region,',
'    p_plugin              in apex_plugin.t_plugin,',
'    p_is_printer_friendly in boolean',
') return apex_plugin.t_region_render_result is',
'',
'    l_result              apex_plugin.t_region_render_result;',
'    c_region_id_container constant varchar2(32767) := apex_escape.html_attribute(p_region.static_id);',
'    c_region_id           constant varchar2(32767) := c_region_id_container || ''-c'';',
'begin',
'    htp.p(''<div id="'' || c_region_id || ''" class="apex-d3-gantt-container"></div>'');',
'',
'    apex_javascript.add_onload_code(''apexD3Gantt(apex, $).initialize({'' ||',
'        apex_javascript.add_attribute(''regionIDContainer'', c_region_id_container, true, true) ||',
'        apex_javascript.add_attribute(''noDataFoundMessage'', p_region.no_data_found_message, true, true) ||',
'        apex_javascript.add_attribute(''regionID'', c_region_id, true, true) ||',
'        apex_javascript.add_attribute(''itemsToSubmit'', apex_plugin_util.page_item_names_to_jquery(p_region.ajax_items_to_submit), true, true) ||',
'        apex_javascript.add_attribute(''escapeHTML'', p_region.escape_output, true, true) ||',
'        apex_javascript.add_attribute(''sidePadding'', p_region.attribute_08, true, true) ||',
'        apex_javascript.add_attribute(''ajaxID'', apex_plugin.get_ajax_identifier, true, false) ||',
'    ''});'');',
'',
'    return l_result;',
'end;',
'',
'function f_ajax (',
'    p_region   in         apex_plugin.t_region,',
'    p_plugin   in         apex_plugin.t_plugin',
') return apex_plugin.t_region_ajax_result is',
'    l_result   apex_plugin.t_region_ajax_result;',
'    l_context apex_exec.t_context;',
'    -- columns',
'    c_task_group_col constant varchar2(32767) := p_region.attribute_01;',
'    c_task_col constant varchar2(32767) := p_region.attribute_02;',
'    c_task_start_col constant varchar2(32767) := p_region.attribute_03;',
'    c_task_end_col constant varchar2(32767) := p_region.attribute_04;',
'    c_task_link_col constant varchar2(32767) := p_region.attribute_05;',
'    c_task_color_col constant varchar2(32767) := p_region.attribute_06;',
'    c_task_tooltip_col constant varchar2(32767) := p_region.attribute_07;',
'    -- column number',
'    l_task_group_col_no pls_integer;',
'    l_task_col_no pls_integer;',
'    l_task_start_col_no pls_integer;',
'    l_task_end_col_no pls_integer;',
'    l_task_link_col_no pls_integer;',
'    l_task_color_col_no pls_integer;',
'    l_task_tooltip_col_no pls_integer;',
'    -- values',
'    l_task_group_value varchar2(32767);',
'    l_task_value varchar2(32767);',
'    l_task_start_value date;',
'    l_task_end_value date;',
'    l_task_link_value varchar2(32767);',
'    l_task_color_value varchar2(32767);',
'    l_task_tooltip_value varchar2(32767);',
'begin',
'    l_context        := apex_exec.open_query_context(p_max_rows => p_region.fetched_rows);',
'',
'    l_task_group_col_no := apex_exec.get_column_position(l_context, c_task_group_col);',
'    l_task_col_no := apex_exec.get_column_position(l_context, c_task_col);',
'    l_task_start_col_no := apex_exec.get_column_position(l_context, c_task_start_col);',
'    l_task_end_col_no := apex_exec.get_column_position(l_context, c_task_end_col);',
'    l_task_link_col_no := apex_exec.get_column_position(l_context, c_task_link_col);',
'    l_task_color_col_no := apex_exec.get_column_position(l_context, c_task_color_col);',
'    l_task_tooltip_col_no := apex_exec.get_column_position(l_context, c_task_tooltip_col);',
'',
'    apex_json.initialize_output;',
'',
'    apex_json.open_object;',
'        apex_json.open_array(''rows'');',
'    ',
'        while apex_exec.next_row(p_context => l_context) loop',
'            ',
'            -- required columns',
'            l_task_group_value := apex_exec.get_varchar2(l_context, l_task_group_col_no);',
'            l_task_value   := apex_exec.get_varchar2(l_context, l_task_col_no);',
'            l_task_start_value   := apex_exec.get_date(l_context, c_task_start_col);',
'            l_task_end_value   := apex_exec.get_date(l_context, c_task_end_col);',
'            -- optional columns',
'            if l_task_link_col_no is not null then',
'                l_task_link_value   := apex_exec.get_varchar2(l_context, l_task_link_col_no);',
'            end if;',
'            if l_task_color_col_no is not null then',
'                l_task_color_value   := apex_exec.get_varchar2(l_context, l_task_color_col_no);',
'            end if;',
'            if l_task_tooltip_col_no is not null then',
'                l_task_tooltip_value   := apex_exec.get_varchar2(l_context, l_task_tooltip_col_no);',
'            end if;',
'',
'            apex_json.open_object;',
'                apex_json.write(''task'', l_task_value);',
'                apex_json.write(''taskType'', l_task_group_value);',
'                apex_json.write(''taskStart'', to_char(l_task_start_value, ''YYYY-MM-DD HH24:MI:SS''));',
'                apex_json.write(''taskEnd'', to_char(l_task_end_value, ''YYYY-MM-DD HH24:MI:SS''));',
'                apex_json.write(''taskLink'', l_task_link_value);',
'                apex_json.write(''taskColor'', l_task_color_value);',
'                apex_json.write(''taskTooltip'', l_task_tooltip_value);',
'            apex_json.close_object;',
'        end loop;',
'        ',
'        apex_exec.close(l_context);',
'    ',
'        apex_json.close_array;',
'    apex_json.close_object;',
'    return l_result;',
'exception',
'    when others then',
'        apex_exec.close(l_context);',
'        apex_json.close_all;',
'        raise;',
'end;'))
